private void Dispose(bool disposing)
        {
            if (!_disposed)
            {
                if (disposing)
                {
                    // ReSharper disable once UseNullPropagation
                    if (_connection != null)
                    {
                        // ReSharper disable once InconsistentlySynchronizedField
                        _connection.Dispose();
                    }
                }

                _disposed = true;
            }
        }

